import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './DirectoryPage.css';

const DirectoryPage = () => {
  const [admins, setAdmins] = useState([]);
  const [residents, setResidents] = useState([]);
  const [staffs, setStaffs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const adminResponse = await fetch('http://localhost:4069/homepage/admin');
        const adminData = await adminResponse.json();
        setAdmins(adminData);
        console.log('Admin data fetched:', adminData);

        const residentStaffResponse = await fetch('http://localhost:4069/homepage/staff');
        const residentStaffData = await residentStaffResponse.json();
        processUsers(residentStaffData); // Process the data as per your structure
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };

    fetchData();
  }, []);

  const processUsers = (data) => {
    const { staffs, residents } = data;

    // Process Staffs
    console.log("Processing Staffs:");
    staffs.forEach((staff) => {
      console.log(`Name: ${staff.name || "N/A"}`);
      console.log(`Username: ${staff.username}`);
      console.log(`Email: ${staff.email}`);
      console.log(`Role: ${staff.role}`);
      console.log(`Contact No: ${staff.contactno}`);
      console.log(`User Type: ${staff.usertype}`);
      console.log("------------");
    });

    // Set staffs state
    setStaffs(staffs);

    // Process Residents
    console.log("\nProcessing Residents:");
    residents.forEach((resident) => {
      console.log(`Name: ${resident.name || "N/A"}`);
      console.log(`Username: ${resident.username}`);
      console.log(`Email: ${resident.email}`);
      console.log(`Flat No: ${resident.flatno}`);
      console.log(`Contact No: ${resident.contactno}`);
      console.log(`Admin: ${resident.admin}`);
      console.log("------------");
    });

    // Set residents state
    setResidents(residents);
  };

  const renderTable = (title, data, columns) => (
    <div className="directory-table">
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              {columns.map((col) => (
                <td key={col}>{item[col.toLowerCase()]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        <h2>Directory</h2>
        {renderTable('Admins', admins, ['Name', 'Email', 'Contact'])}
        {renderTable('Residents', residents, ['Name', 'Email', 'Contact', 'Flat'])}
        {renderTable('Staffs', staffs, ['Name', 'Email', 'Contact', 'Role'])}
      </div>
    </div>
  );
};

export default DirectoryPage;
