import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './MaintenancePage.css';
import { getUserFromStorage } from './utils';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // Adjust the base URL as needed

const MaintenancePage = () => {
  const [requests, setRequests] = useState([]);
  const [serviceType, setServiceType] = useState('');
  const [description, setDescription] = useState('');
  const user = getUserFromStorage(); // Use function from utils.js

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/services`);
        setRequests(response.data.serviceRequests);
      } catch (error) {
        console.error('Failed to fetch service requests:', error);
      }
    };

    fetchRequests();
  }, []);

  if (!user) {
    return <div>Please log in to view this page.</div>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${url}/services/${serviceType}`, { description });
      setRequests([...requests, response.data.service]);
    } catch (error) {
      console.error('Failed to post service request:', error);
    }
  };

  const handleUpdate = async (token) => {
    try {
      const response = await axios.put(`${url}/homepage/services/${token}`, { description });
      setRequests(requests.map(request => request.token === token ? response.data : request));
    } catch (error) {
      console.error('Failed to update service request:', error);
    }
  };

  const handleDelete = async (token) => {
    try {
      await axios.delete(`${url}/homepage/services/${token}`);
      setRequests(requests.filter(request => request.token !== token));
    } catch (error) {
      console.error('Failed to delete service request:', error);
    }
  };

  const handleResolve = async (token) => {
    try {
      await axios.put(`${url}/home/services/${token}`);
      setRequests(requests.filter(request => request.token !== token));
    } catch (error) {
      console.error('Failed to resolve service request:', error);
    }
  };

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        {user.role === 'resident' ? (
          <div className="resident-view">
            <div className="post-request">
              <h2>Request Maintenance Service</h2>
              <form onSubmit={handleSubmit}>
                <label>
                  Service Type:
                  <select value={serviceType} onChange={(e) => setServiceType(e.target.value)}>
                    <option value="electric">Electric</option>
                    <option value="plumbing">Plumbing</option>
                    <option value="others">Others</option>
                  </select>
                </label>
                <label>
                  Description:
                  <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </label>
                <button type="submit">Submit Request</button>
              </form>
            </div>
            <div className="view-requests">
              <h2>Posted Requests</h2>
              {requests.map((request) => (
                <div key={request.token} className="request-item">
                  <p><strong>Type:</strong> {request.serviceType}</p>
                  <p><strong>Description:</strong> {request.description}</p>
                  <button onClick={() => handleUpdate(request.token)}>Update</button>
                  <button onClick={() => handleDelete(request.token)}>Delete</button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="maintenance-view">
            <h2>Maintenance Requests</h2>
            {requests.map((request) => (
              <div key={request.token} className="request-item">
                <p><strong>Resident Name:</strong> {request.name}</p>
                <p><strong>Contact:</strong> {request.contact}</p>
                <p><strong>Flat Number:</strong> {request.flat}</p>
                <p><strong>Type:</strong> {request.serviceType}</p>
                <p><strong>Description:</strong> {request.description}</p>
                <button onClick={() => handleResolve(request.token)}>Resolve</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Maintenance;
