import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar';
import './MaintenancePage.css';
import { getUserFromStorage } from './utils.js';

const MaintenancePage = () => {
  const [user, setUser] = useState(null);
  const [requests, setRequests] = useState([]);
  const [issueDescription, setIssueDescription] = useState('');
  const [issueLocation, setIssueLocation] = useState('');
  const [priority, setPriority] = useState('Low');

  useEffect(() => {
    const token = getUserFromStorage();

    if (!token) {
      // Redirect to login if no user is found
      window.location.href = '/society/login';
      return;
    }

    setUser({ token }); // Setting the token in the user state

    const fetchRequests = async () => {
      try {
        const response = await axios.get('http://localhost:4069/api/maintenanceRequests', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        setRequests(response.data);
        console.log('Maintenance requests fetched:', response.data);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };

    fetchRequests();
  }, []);

  const handleCreateRequest = async () => {
    const token = user.token;
    const newRequest = { issueDescription, issueLocation, priority };

    try {
      const response = await axios.post('http://localhost:4069/api/maintenanceRequests', newRequest, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Create request response:', response.data);
      setRequests([...requests, response.data.maintenanceRequest]);
      setIssueDescription('');
      setIssueLocation('');
      setPriority('Low');
      console.log('Maintenance request created:', response.data.maintenanceRequest);
    } catch (error) {
      console.error('Failed to create request:', error);
      alert(`Failed to create request: ${error.response ? error.response.data.error : error.message}`);
    }
  };

  const handleUpdateRequest = async (requestId, updatedDescription) => {
    const token = user.token;

    try {
      const response = await axios.put(`http://localhost:4069/api/maintenanceRequests/${requestId}/update`, { description: updatedDescription }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setRequests(requests.map(request => request._id === requestId ? response.data.maintenanceRequest : request));
      console.log('Maintenance request updated:', response.data.maintenanceRequest);
    } catch (error) {
      console.error('Failed to update request:', error);
    }
  };

  const handleResolveRequest = async (requestId) => {
    const token = user.token;

    try {
      const response = await axios.put(`http://localhost:4069/api/maintenanceRequests/${requestId}/resolve`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setRequests(requests.map(request => request._id === requestId ? response.data.maintenanceRequest : request));
      console.log('Maintenance request resolved:', response.data.maintenanceRequest);
    } catch (error) {
      console.error('Failed to resolve request:', error);
    }
  };

  const handleDeleteRequest = async (requestId) => {
    const token = user.token;

    try {
      await axios.delete(`http://localhost:4069/api/maintenanceRequests/${requestId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setRequests(requests.filter(request => request._id !== requestId));
      console.log('Maintenance request deleted:', requestId);
    } catch (error) {
      console.error('Failed to delete request:', error);
    }
  };

  const renderRequests = () => (
    <div className="requests-list">
      {requests.map(request => (
        <div key={request._id} className="request-item">
          <p><strong>Description:</strong> {request.issueDescription}</p>
          <p><strong>Location:</strong> {request.issueLocation}</p>
          <p><strong>Priority:</strong> {request.priority}</p>
          <button onClick={() => handleUpdateRequest(request._id, prompt('Update description:', request.issueDescription))}>Update</button>
          <button onClick={() => handleDeleteRequest(request._id)}>Delete</button>
          <button onClick={() => handleResolveRequest(request._id)}>Resolve</button>
        </div>
      ))}
    </div>
  );

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        <h2>Maintenance Requests</h2>
        <div className="content-sections">
          <div className="create-request-section">
            <div className="create-request-form">
              <h3>Create a Maintenance Request</h3>
              <label>
                Description:
                <textarea value={issueDescription} onChange={(e) => setIssueDescription(e.target.value)}></textarea>
              </label>
              <label>
                Location:
                <input type="text" value={issueLocation} onChange={(e) => setIssueLocation(e.target.value)} />
              </label>
              <label>
                Priority:
                <select value={priority} onChange={(e) => setPriority(e.target.value)}>
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </label>
              <button onClick={handleCreateRequest}>Submit Request</button>
            </div>
          </div>
          <div className="view-requests-section">
            {renderRequests()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MaintenancePage;
