import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './Maintenance.css';
import { getUserFromStorage } from './utils';

const Maintenance = () => {
  const [requests, setRequests] = useState([]);
  const [serviceType, setServiceType] = useState('');
  const [description, setDescription] = useState('');
  const user = getUserFromStorage();

  useEffect(() => {
    // Fetch posted requests from storage or backend
    const storedRequests = localStorage.getItem('maintenanceRequests');
    if (storedRequests) {
      setRequests(JSON.parse(storedRequests));
    }
    console.log('User data:', user);
  }, []);

  if (!user) { 
    return <div>Please log in to view this page.</div>;
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = Date.now(); // Simple token generation using timestamp
    const newRequest = { token, serviceType, description, name: user.name, contact: user.contact, flat: user.flat };

    const updatedRequests = [...requests, newRequest];
    setRequests(updatedRequests);
    localStorage.setItem('maintenanceRequests', JSON.stringify(updatedRequests));
  };

  const handleUpdate = (token) => {
    const updatedRequests = requests.map(request => 
      request.token === token ? { ...request, serviceType, description } : request
    );
    setRequests(updatedRequests);
    localStorage.setItem('maintenanceRequests', JSON.stringify(updatedRequests));
  };

  const handleDelete = (token) => {
    const updatedRequests = requests.filter(request => request.token !== token);
    setRequests(updatedRequests);
    localStorage.setItem('maintenanceRequests', JSON.stringify(updatedRequests));
  };

  const handleResolve = (token) => {
    const updatedRequests = requests.filter(request => request.token !== token);
    setRequests(updatedRequests);
    localStorage.setItem('maintenanceRequests', JSON.stringify(updatedRequests));
  };

  if (!user) {
    return <div>Please log in to view this page.</div>;
  }

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        {user.role === 'resident' ? (
          <div className="resident-view">
            <div className="post-request">
              <h2>Request Maintenance Service</h2>
              <form onSubmit={handleSubmit}>
                <label>
                  Service Type:
                  <select value={serviceType} onChange={(e) => setServiceType(e.target.value)}>
                    <option value="electric">Electric</option>
                    <option value="plumbing">Plumbing</option>
                    <option value="others">Others</option>
                  </select>
                </label>
                <label>
                  Description:
                  <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
                </label>
                <button type="submit">Submit Request</button>
              </form>
            </div>
            <div className="view-requests">
              <h2>Posted Requests</h2>
              {requests.map((request) => (
                <div key={request.token} className="request-item">
                  <p><strong>Type:</strong> {request.serviceType}</p>
                  <p><strong>Description:</strong> {request.description}</p>
                  <button onClick={() => handleUpdate(request.token)}>Update</button>
                  <button onClick={() => handleDelete(request.token)}>Delete</button>
                </div>
              ))}
            </div>
          </div>
        ) : (
          <div className="maintenance-view">
            <h2>Maintenance Requests</h2>
            {requests.map((request) => (
              <div key={request.token} className="request-item">
                <p><strong>Resident Name:</strong> {request.name}</p>
                <p><strong>Contact:</strong> {request.contact}</p>
                <p><strong>Flat Number:</strong> {request.flat}</p>
                <p><strong>Type:</strong> {request.serviceType}</p>
                <p><strong>Description:</strong> {request.description}</p>
                <button onClick={() => handleResolve(request.token)}>Resolve</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Maintenance;
