// src/RegisterDetailsPage.js
import React, { useState } from 'react';
import { useLocation, useHistory } from 'react-router-dom';

const RegisterDetailsPage = () => {
  const location = useLocation(); // To access passed state (email)
  const history = useHistory();
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [flatNumber, setFlatNumber] = useState('');
  const [role, setRole] = useState('');
  const [error, setError] = useState('');

  // Retrieve the email passed from the previous page
  const { email } = location.state || {};

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form
    if (!name || !phone || !flatNumber || !role) {
      setError('Please fill in all fields.');
      return;
    }

    // Handle user registration (e.g., send data to backend)
    console.log('Email:', email);
    console.log('Name:', name);
    console.log('Phone:', phone);
    console.log('Flat Number:', flatNumber);
    console.log('Role:', role);

    // After successful registration, you could redirect to the login page or home page
    history.push('/home'); // Example redirect after registration
  };

  return (
    <div className="register-details-page">
      <h2>Complete Your Registration</h2>
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your full name"
            required
          />
        </label>
        <label>
          Phone Number:
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="Enter your phone number"
            required
          />
        </label>
        <label>
          Flat Number:
          <input
            type="text"
            value={flatNumber}
            onChange={(e) => setFlatNumber(e.target.value)}
            placeholder="Enter your flat number"
            required
          />
        </label>
        <label>
          Role:
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="">Select your role</option>
            <option value="Member">Member</option>
            <option value="Admin">Admin</option>
          </select>
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default RegisterDetailsPage;
