export const getUserFromStorage = () => {
    try {
      const user = localStorage.getItem('user');
      if (!user) return null;
  
      const parsedInfo = JSON.parse(user);
      return parsedInfo; // Return the entire user object
    } catch (error) {
      console.error('Failed to fetch user from storage:', error);
      return null; // Ensure the function returns null in case of an error
    }
  };
  
  export const setUserInStorage = (user) => {
    try {
      localStorage.setItem('user', JSON.stringify(user));
    } catch (error) {
      console.error('Failed to save user to storage:', error);
    }
  };
  
  export const removeUserFromStorage = () => {
    try {
      localStorage.removeItem('user');
    } catch (error) {
      console.error('Failed to remove user from storage:', error);
    }
  };
