// src/EmailLoginPage.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // To redirect after submitting

const EmailLoginPage = () => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const history = useHistory(); // For programmatic navigation

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation for email
    if (!email) {
      setError('Please enter your email.');
      return;
    }

    // Redirect to the next page where user can enter additional details
    history.push('/register-details', { email }); // Passing email to the next page
  };

  return (
    <div className="email-login-page">
      <h2>Register with Email</h2>
      {error && <div className="error-message">{error}</div>}
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
            required
          />
        </label>
        <button type="submit">Next</button>
      </form>
    </div>
  );
};

export default EmailLoginPage;
