export const getUserFromStorage = () => {
    try {
      const user = localStorage.getItem('user');
      if (!user) return null;

      const parsedInfo = JSON.parse(user);
      return parsedInfo;
    } catch (error) {
        console.error('Failed to fetch user from storage:', error);
        return null;
    }
  }
