import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './Directory.css';

// Function to get user from storage
const getUserFromStorage = () => {
  const user = localStorage.getItem('user');
  return user ? JSON.parse(user) : null;
};

const DirectoryPage = () => {
  const [admins, setAdmins] = useState([]);
  const [residents, setResidents] = useState([]);
  const [staffs, setStaffs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const user = getUserFromStorage();

      if (!user) {
        // Redirect to login if no user is found
        window.location.href = '/society/login';
        return;
      }

      const token = user.token;

      try {
        const adminResponse = await fetch('http://localhost:5000/society/homepage/admin', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (!adminResponse.ok) {
          throw new Error('Failed to fetch admin data');
        }
        const adminData = await adminResponse.json();
        setAdmins(adminData);
        console.log('Admin data fetched:', adminData);

        const residentStaffResponse = await fetch('http://localhost:5000/api/residents-staffs', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        if (!residentStaffResponse.ok) {
          throw new Error('Failed to fetch resident and staff data');
        }
        const residentStaffData = await residentStaffResponse.json();
        console.log('Resident and staff data fetched:', residentStaffData);

        // Process and set residents and staffs data
        setResidents(residentStaffData.residents);
        setStaffs(residentStaffData.staffs);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };

    fetchData();
  }, []);

  const renderTable = (title, data, columns) => (
    <div className="directory-table">
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              {columns.map((col) => (
                <td key={col}>{item[col.toLowerCase()]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        <h2>Directory</h2>
        {renderTable('Admins', admins, ['Name', 'Email', 'Contact'])}
        {renderTable('Residents', residents, ['Name', 'Email', 'Contact', 'Flatno'])}
        {renderTable('Staffs', staffs, ['Name', 'Email', 'Contact', 'Role'])}
      </div>
    </div>
  );
};

export default DirectoryPage;
