import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Maintenance.css';
import Sidebar from './Sidebar';


const Maintenance = () => {
    const [requests, setRequests] = useState([]);
    const [serviceType, setServiceType] = useState('');
    const [description, setDescription] = useState('');
    const [user, setUser] = useState(null); // To hold user info

    useEffect(() => {
        fetchUser();
        fetchRequests();
    }, []);

    const fetchUser = async () => {
        try {
            const response = await axios.get('http://localhost:4069/society/getUser');
            setUser(response.data.user);
        } catch (error) {
            console.error("Error fetching user data:", error);
        }
    };

    const fetchRequests = async () => {
        try {
            const response = await axios.get('http://localhost:4069/society/homepage/services');
            if (response.status === 200){
              setRequests(response.data.serviceRequests);
            } else{
              console.error("Error fetching service requests:", response.status);
            }
          } catch (error) {
            console.error("Error fetching service requests:", error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:4069/society/homepage/services', {
                serviceType,
                description,
            });
            setRequests([...requests, response.data.service]);
            setServiceType('');
            setDescription('');
        } catch (error) {
            console.error("Error posting service request:", error);
        }
    };

    const handleUpdate = async (id, updatedDescription) => {
        try {
            const response = await axios.put(`http://localhost:4069/society/homepage/services/${id}`, {
                description: updatedDescription,
            });
            const updatedRequests = requests.map((req) =>
                req._id === id ? { ...req, description: updatedDescription } : req
            );
            setRequests(updatedRequests);
        } catch (error) {
            console.error("Error updating service request:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4069/society/homepage/services/${id}`);
            setRequests(requests.filter((req) => req._id !== id));
        } catch (error) {
            console.error("Error deleting service request:", error);
        }
    };

    const handleResolve = async (id) => {
        try {
            await axios.put(`http://localhost:4069/society/homepage/services/${id}`);
            setRequests(requests.filter((req) => req._id !== id));
        } catch (error) {
            console.error("Error resolving service request:", error);
        }
    };

    if (!user) return <div>Loading...</div>;

    return (
        <div className="maintenance-page">
            <Sidebar />
            <main>
                {user.usertype === 'resident' && (
                    <div className="request-form" style={{ width: '60%' }}>
                        <form onSubmit={handleSubmit}>
                            <h2>Post a Service Request</h2>
                            <div>
                                <label>Service Type</label>
                                <select value={serviceType} onChange={(e) => setServiceType(e.target.value)}>
                                    <option value="Electrician">Electrician</option>
                                    <option value="Plumber">Plumber</option>
                                    <option value="Other">Other</option>
                                </select>
                            </div>
                            <div>
                                <label>Description</label>
                                <textarea
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                ></textarea>
                            </div>
                            <button type="submit">Submit</button>
                        </form>
                    </div>
                )}
                <div className="request-list" style={{ width: '40%' }}>
                    <h2>Service Requests</h2>
                    <ul>
                        {requests.map((req) => (
                            <li key={req._id}>
                                <p>Type: {req.serviceType}</p>
                                <p>Description: {req.description}</p>
                                {user.usertype === 'resident' && (
                                    <>
                                        <button onClick={() => handleUpdate(req._id, 'Updated Description')}>
                                            Update
                                        </button>
                                        <button onClick={() => handleDelete(req._id)}>Delete</button>
                                    </>
                                )}
                                {user.usertype === 'maintenance' && (
                                    <>
                                        <p>Flat: {req.user.flatno}</p>
                                        <p>Resident: {req.user.name}</p>
                                        <button onClick={() => handleResolve(req._id)}>Resolve</button>
                                    </>
                                )}
                            </li>
                        ))}
                    </ul>
                </div>
            </main>
        </div>
    );
};

export default Maintenance;
