import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import './Directory.css';

const API_BASE_URL = 'http://localhost:4069/society/homepage'; // Adjust the base URL as needed

const DirectoryPage = () => {
  const [admins, setAdmins] = useState([]);
  const [residents, setResidents] = useState([]);
  const [staffs, setStaffs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching admin data...');
        const adminResponse = await fetch(`${API_BASE_URL}/admin`);
        const adminText = await adminResponse.text();
        console.log('Admin response:', adminText);

        const adminData = JSON.parse(adminText);
        console.log('Admin data fetched:', adminData);

        console.log('Fetching resident and staff data...');
        const residentStaffResponse = await fetch(`${API_BASE_URL}/staff`);
        const residentStaffText = await residentStaffResponse.text();
        console.log('Resident and staff response:', residentStaffText);

        const residentStaffData = JSON.parse(residentStaffText);
        console.log('Resident and staff data fetched:', residentStaffData);

        setAdmins(adminData);
        setResidents(residentStaffData.residents);
        setStaffs(residentStaffData.staffs);
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };

    fetchData();
  }, []);

  const renderTable = (title, data, columns) => (
    <div className="directory-table">
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              {columns.map((col) => (
                <td key={col}>{item[col.toLowerCase()]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  console.log('Admins:', admins);
  console.log('Residents:', residents);
  console.log('Staffs:', staffs);

  return (
    <div className="page-container">
      <Sidebar />
      <div className="main-content">
        <h2>Directory</h2>
        {renderTable('Admins', admins, ['Name', 'Email', 'Contact'])}
        {renderTable('Residents', residents, ['Name', 'Email', 'Contact', 'Flat'])}
        {renderTable('Staffs', staffs, ['Name', 'Email', 'Contact', 'Role'])}
      </div>
    </div>
  );
};

export default DirectoryPage;
